"use strict";function formatItem(n){return n.name+": $"+n.unitPrice}function find(){var n=$("#prodId").val();$.getJSON(productUri+"/"+n).done(function(n){$("#product").text(formatItem(n))}).fail(function(n,t,i){$("#product").text("Error: "+i)})}const productUri="https://localhost:44321/api/product",categoryUri="https://localhost:44321/api/category";$(document).ready(function(){$.getJSON(productUri).done(function(n){$.each(n,function(n,t){$("<tr>").append($("<td>").text(t.name),$("<td>").text(t.quantityPerUnit),$("<td>").text(t.unitPrice),$("<td>").text(t.unitsInStock)).appendTo($("#products"))})}).fail(function(n,t,i){$("#products").text("Error: "+i)})});$(document).ready(function(){$.getJSON(categoryUri).done(function(n){$.each(n,function(n,t){$("<tr>").append($("<td>").text(t.name),$("<td>").text(t.description)).appendTo($("#categories"))})}).fail(function(n,t,i){$("#categories").text("Error: "+i)})});